meta.script:
    function ToggleView(LayerToToggle)
    {
        if (document.getElementById) {
            var StyleToChange = document.getElementById(LayerToToggle).style;
            StyleToChange.display = StyleToChange.display? "":"block";
        } else if (document.all) {
            var StyleToChange = document.all[LayerToToggle].style;
            StyleToChange.display = StyleToChange.display? "":"block";
        } else if (document.layers) {
            var StyleToChange = document.layers[LayerToToggle].style;
            StyleToChange.display = StyleToChange.display? "":"block";
        }
    }

